import React, { useContext, useEffect, useState } from "react";

const API_URL = `https://www.omdbapi.com/?apikey=e23eba27&s=${query}`;
const AppContext =  React.createContext();

//we need to create provider function
const AppProvider = ({children}) => {
    const [isLoading, setIsLoading] = useState(true);
    const [movie, setMovie] = useState([]);
    const [isError, setIsError] = useState({show:"false", msg:""});
    const [query, setQuery] = useState("titanic");
    const getMovies = async (url) => {
        try{
            const res = await fetch(url);
            const data = await res.json();
            // console.log(data);
           
            if(data.Response === "True"){
                setIsLoading(false);
                setMovie(data.Search);
                
                
            }
           
            else{
              setIsError({
                show:true,
                msg: data.error,
              })
            }
          
            
        } 
        catch(error){
            console.log(error);
        }
    }
    useEffect(() => {
        getMovies(API_URL);
    },[]);
   
    //the app that we wrapped we cat get that data so we use children
        return<AppContext.Provider value={{isLoading, isError, movie}}>
            {children}
        </AppContext.Provider>
}

//global context hooks (Alternative method)
const useGlobalContext  = () => {
    return useContext(AppContext);
}
export {AppContext, AppProvider, useGlobalContext};
