import React, { useContext, useEffect, useState } from "react";

const API_URL = `https://www.omdbapi.com/?apikey=e23eba27&s=godfather`;
const AppContext =  React.createContext();

//we need to create provider function
const AppProvider = ({children}) => {
    const [isLoading, setIsLoading] = useState(true);
    const [movie, setMovie] = useState([]);
    const getMovies = async (url) => {
        try{
            const res = await fetch(url);
            const data = res.json();
            console.log(data);
            console.log(movie);
        } 
        catch(error){
            console.log(error);
        }
    }
    useEffect(() => {
        getMovies(API_URL);
    },[]);
    //the app that we wrapped we cat get that data so we use children
        return<AppContext.Provider value="Anup">
            {children}
        </AppContext.Provider>
}

//global context hooks (Alternative method)
const useGlobalContext  = () => {
    return useContext(AppContext);
}
export {AppContext, AppProvider, useGlobalContext};
